	<script>
	jQuery( document ).ready(function($) {
			$('#of_load_preset').click(function(){
				$('#add-preset').slideDown();
			});

			

			$('a.preset').click(function(){
				var settings = $(this).find('textarea').val();
				$('#export_data').val(settings);
				$('.backupandimport').click();
				$('#of_import_button').click();

			});

			// PRESETS
			$('.section-presets .button-primary').click(function(e){
				$(this).parent().find('.pre_select_wrapper').slideDown();
				e.preventDefault();
			});

			$('.section-presets .cancel').click(function(e){
				$(this).parent().slideUp();
				e.preventDefault();
			});
			
			$('a[data-preset]').click(function(e){
				var preset = $(this).data('preset');
				if(preset == 'header_1') { 
					value = ""
				}
				
				$('#export_data').val(value);
				$('#of_import_button').click();
				$(this).parent().slideUp();
				e.preventDefault();
			});

	});
